#include "minishell.h"

void	unset_env(char *cmd)
{
	char	**params;
	int		i;

	ft_strsplit(cmd, ' ', &params);
	i = 1;
	while (params[i])
	{
		unsetenv(params[i]);
		i++;
	}
}
